mapper.update:
update weny_purch_record
set
 exchange_state = 1,
  lutime = #{lutime,jdbcType=VARCHAR}
where sn = #{sn,jdbcType=VARCHAR}

update weny_purch_record
set
 exchange_state = 2,
  lutime = #{lutime,jdbcType=VARCHAR}
where sn = #{sn,jdbcType=VARCHAR}

update weny_purch_record
set
  lutime = #{lutime,jdbcType=VARCHAR},
  `status` = #{status,jdbcType=INTEGER},
  message = #{message,jdbcType=VARCHAR}
where sn = #{sn,jdbcType=VARCHAR}

mapper.select:
select

from weny_purch_record
where person = #{person,jdbcType=VARCHAR}
and bankid=#{bankid}
order by ctime asc
limit #{offset},#{limit}

select

from weny_purch_record
where person = #{person,jdbcType=VARCHAR}
and bankid=#{bankid}
and state=1
and exchange_state=0
order by ctime asc
limit #{offset},#{limit}

select

from weny_purch_record
where person = #{person,jdbcType=VARCHAR}
and bankid=#{bankid}
and state=1
and exchange_state=2
order by ctime desc
limit #{offset},#{limit}
